mapper.select:
select  from pf_result


		and t_id = #{tId,jdbcType=BIGINT}


		and create_time >= #{startCreateTime, jdbcType=TIMESTAMP}


		and create_time <= #{endCreateTime, jdbcType=TIMESTAMP}


		and catagory = #{catagory,jdbcType=INTEGER}


	 	and exp_no = #{expNo,jdbcType=INTEGER}





		order by ${custom_order_sql}


		order by id desc
