mapper.select:
  	select 

      from vehicle


	        and code like concat('%', #{code,jdbcType=VARCHAR}, '%') 



	        and type like concat('%', #{type,jdbcType=VARCHAR}, '%') 



			and state = #{state,jdbcType=INTEGER}



			and state != #{notstate,jdbcType=INTEGER}



	    	and pro_time >= #{startProTime, jdbcType=DATE}



	    	and pro_time <= #{endProTime, jdbcType=DATE}



	    	and create_time >= #{startCreateTime, jdbcType=TIMESTAMP}



	    	and create_time <= #{endCreateTime, jdbcType=TIMESTAMP}



	        and code = #{ecode,jdbcType=VARCHAR}



	        and type = #{etype,jdbcType=VARCHAR}



	    	and pro_time = #{eProTime, jdbcType=DATE}



			and remark = #{remark,jdbcType=VARCHAR}



			and pro_addr = #{proAddr,jdbcType=VARCHAR}



			and id != #{eid,jdbcType=BIGINT}






  			order by ${custom_order_sql}


  			order by id desc
